if ($.fn.pagination){	$.fn.pagination.defaults.beforePageText = 'Pagina';	$.fn.pagination.defaults.afterPageText = 'din {pagini}';	$.fn.pagination.defaults.displayMsg = 'Arata {de la} la {la} din {total} articole';}if ($.fn.datagrid){	$.fn.datagrid.defaults.loadMsg = 'Proceseaza,va rugam asteptati ...';}if ($.fn.treegrid && $.fn.datagrid){	$.fn.treegrid.defaults.loadMsg = $.fn.datagrid.defaults.loadMsg;}if ($.messager){	$.messager.defaults.ok = 'Ok';	$.messager.defaults.cancel = 'Sterge';}$.map(['validatebox','textbox','passwordbox','filebox','searchbox',		'combo','combobox','combogrid','combotree',		'datebox','datetimebox','numberbox',		'spinner','numberspinner','timespinner','datetimespinner'], function(plugin){	if ($.fn[plugin]){		$.fn[plugin].defaults.missingMessage = 'Acest camp este obigatoriu.';	}});if ($.fn.validatebox){	$.fn.validatebox.defaults.rules.email.message = 'Intoduceti o adresa de email valida.';	$.fn.validatebox.defaults.rules.url.message = 'Adaugati un URL valid.';	$.fn.validatebox.defaults.rules.length.message = 'Adaugati o valoare intre {0} si {1}.';	$.fn.validatebox.defaults.rules.remote.message = 'Remediati acest camp.';}if ($.fn.calendar){	$.fn.calendar.defaults.weeks = ['D','L','M','M','J','V','S'];	$.fn.calendar.defaults.months = ['Ian', 'Feb', 'Mar', 'Apr', 'Mai', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Noi', 'Dec'];}if ($.fn.datebox){	$.fn.datebox.defaults.currentText = 'Astazi';	$.fn.datebox.defaults.closeText = 'Inchide';	$.fn.datebox.defaults.okText = 'Ok';}if ($.fn.datetimebox && $.fn.datebox){	$.extend($.fn.datetimebox.defaults,{		currentText: $.fn.datebox.defaults.currentText,		closeText: $.fn.datebox.defaults.closeText,		okText: $.fn.datebox.defaults.okText	});}